Speller Code <pre><code>// Implements a dictionary's functionality

#include &lt;stdbool.h&gt;
#include "ctype.h"
#include "stdio.h"
#include "string.h"
#include "strings.h"
#include "stdlib.h"
#include "cs50.h"

#include "dictionary.h"

// Represents a node in a hash table
typedef struct node
{
char word[LENGTH + 1];
struct node *next;
}
node;

// Number of buckets in hash table
const unsigned int N = 25;    //index for int N for A-Z
int total_words = 0;          //counts the total words from dictionary
bool is_loaded = false;       //makes sure that dictionary loads

// Hash table
node *table[N];

// Returns true if word is in dictionary else false
bool check(const char *word)
{
int i = hash(word);
if (table[i] == NULL)
{
return false;
}
for (node *cursor = table[i]; cursor != NULL; cursor = cursor-&gt;next)
{
if (strcasecmp(word, cursor-&gt;word) == 0) //compare strings
{
return true;
}
}
return false;
}

// Hashes word to a number
unsigned int hash(const char *word) //create hash code using firt letter
{
char x;
x = tolower(word[0]);
int hash = x;
hash = hash - 97;
return hash;
}

// Loads dictionary into memory, returning true if successful else false
bool load(const char *dictionary)
{
FILE *d = fopen(dictionary, "r");
if (d == NULL) //check if file opened or not
{
printf("File not found\n");
return false;
}

for (int k = 0; k &lt; N; k++)
{
table[k] = NULL; //set all pointers in hash table to NULL
}

char new_word[LENGTH + 1];

while (fscanf(d, "%s", new_word) != EOF)
{
node *new_node = malloc(sizeof(node));

if (new_node == NULL)
{
return false;
}

int i = hash(new_word);
strcpy(new_node-&gt;word, new_word);

if (table[i] == NULL)
{
new_node-&gt;next = NULL;
table[i] = new_node;
total_words++;
}

else
{
new_node-&gt;next = table[i];
table[i] = new_node;
total_words++;
}
}
fclose(d);
is_loaded = true;
return true;
}

// Returns number of words in dictionary if loaded else 0 if not yet loaded
unsigned int size(void)
{
if (is_loaded == true)
{
return total_words;
}
else
{
return 0;
}
}
// Unloads dictionary from memory, returning true if successful else false
bool unload(void)
{
for (int i = 0; i &lt;= N; i++)
{
if (table[i] != NULL)
{
node *p = table[i];
while (p != NULL)
{
node *q = p;
p = p-&gt;next;
free(q);
}
}
}
return true;
}
